export function initAccordion(){const accordionItems=document.querySelectorAll(".accordion-item");accordionItems.forEach((function(item,index){item.querySelector(".accordion-header").addEventListener("click",(function(){item.classList.contains("active")?item.classList.remove("active"):item.classList.add("active"),accordionItems.forEach((function(otherItem,otherIndex){otherIndex!==index&&otherItem.classList.remove("active")}))})),0===index&&item.classList.add("active")}))}