export function initCarousel(containerId,trackId,prevButtonId,nextButtonId,paginationId){const container=document.getElementById(containerId),track=document.getElementById(trackId),prevButton=document.getElementById(prevButtonId),nextButton=document.getElementById(nextButtonId),pagination=document.getElementById(paginationId);if(!(container&&track&&prevButton&&nextButton))return void console.error("Error: Carousel elements not found.");const slides=track.children;let index=0;const autoplayInterval=7e3;function updateCarousel(){const offset=100*-index+"%";track.style.transition="transform 1s ease",track.style.transform="translateX("+offset+")",pagination&&function(){if(!pagination||!pagination.children||0===pagination.children.length)return void function(){if(!pagination)return;pagination.innerHTML="";for(let i=0;i<slides.length;i++){const dot=document.createElement("div");dot.classList.add("dot"),i===index&&dot.classList.add("active"),dot.addEventListener("click",(()=>{index=i,updateCarousel(),resetAutoplay()})),pagination.appendChild(dot)}}();for(let dot of pagination.children)dot.classList.remove("active");pagination.children[index].classList.add("active")}(),setTimeout((()=>{track.style.transition=""}),1e3)}function autoplay(){index=(index+1)%slides.length,updateCarousel()}function resetAutoplay(){clearInterval(autoplayIntervalId),autoplayIntervalId=setInterval(autoplay,autoplayInterval)}prevButton.addEventListener("click",(()=>{index=(index-1+slides.length)%slides.length,updateCarousel(),resetAutoplay()})),nextButton.addEventListener("click",(()=>{index=(index+1)%slides.length,updateCarousel(),resetAutoplay()}));let autoplayIntervalId=setInterval(autoplay,autoplayInterval);updateCarousel()}