export function initReadMore(){const readMoreButtons=document.querySelectorAll(".read-more"),moreContents=document.querySelectorAll(".more-content");function openReadMoreDefault(){window.innerWidth>=992?(moreContents.forEach((moreContent=>{moreContent.style.display="inline",moreContent.classList.add("show-more")})),readMoreButtons.forEach((readMoreButton=>{readMoreButton.style.display="none"}))):(moreContents.forEach((moreContent=>{moreContent.style.display="none",moreContent.classList.remove("show-more")})),readMoreButtons.forEach((readMoreButton=>{readMoreButton.style.display="inline",readMoreButton.textContent="Show more"})))}document.addEventListener("click",(function(event){if(event.target.matches(".read-more")){event.preventDefault();const readMoreButton=event.target,moreContent=readMoreButton.previousElementSibling.querySelector(".more-content");"none"===moreContent.style.display||""===moreContent.style.display?(moreContent.style.display="inline",readMoreButton.textContent="Show less",moreContent.classList.add("show-more")):(moreContent.style.display="none",readMoreButton.textContent="Show more",moreContent.classList.remove("show-more"))}})),openReadMoreDefault(),window.addEventListener("resize",openReadMoreDefault)}