const colorMap={brief_border:"#b2abff",research_border:"#ff9494",IA_border:"#7aafff",Inter_border:"#ffe079",LoFi_border:"#9efbe6",desSystem_border:"#c5ffff",HiFi_border:"#86ffb8",uxTest_border:"#21b1c9",results_border:"#ffe7b0",conclusion_border:"#c6ffc6"};export function scrollHighlight(){const sections=document.querySelectorAll("section"),sidebarItems=document.querySelectorAll(".side-bar-list-item"),handleScroll=()=>{sections.forEach((section=>{const sectionTop=section.offsetTop,sectionBottom=sectionTop+section.offsetHeight;if(window.scrollY+window.innerHeight>=sectionTop&&window.scrollY<=sectionBottom){const sectionId=section.getAttribute("id"),sectionColorName=section.getAttribute("data-color"),sectionColor=colorMap[sectionColorName];sectionColor&&((sectionId,color)=>{sidebarItems.forEach((item=>{const targetId=item.querySelector("a").getAttribute("href").substring(1);item.style.backgroundColor=targetId===sectionId?color:""}))})(sectionId,sectionColor)}}))};window.addEventListener("scroll",handleScroll),handleScroll()}