export function initializeTabModules(){document.querySelectorAll(".tab-container").forEach((tabContainer=>{try{const tabButtons=tabContainer.querySelectorAll(".tab-buttons .tab-button"),tabContents=tabContainer.querySelectorAll(".tab-contents .tab-module");let activeTabIndex=parseInt(sessionStorage.getItem("activeTabIndex"))||0;(activeTabIndex>=tabContents.length||activeTabIndex<0)&&(activeTabIndex=0),tabContents.forEach(((content,index)=>{index!==activeTabIndex&&(content.style.display="none")})),tabContents[activeTabIndex].style.display="flex",tabButtons[activeTabIndex].classList.add("active"),tabButtons.forEach(((tabButton,index)=>{tabButton.addEventListener("click",(()=>{tabContents.forEach((content=>{content.style.display="none"})),tabButtons.forEach((button=>{button.classList.remove("active")})),tabContents[index].style.display="flex",tabButton.classList.add("active"),sessionStorage.setItem("activeTabIndex",index)}))}))}catch(error){console.error("Error initializing tab modules:",error)}}))}